import React from "react";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import "./App.css";
import { Button, Typography } from "@material-ui/core";

function changeEditor(x) {
  let submitButton = document.getElementById("submitButton");
  let rustButton = document.getElementById("rustButton");
  let cppButton = document.getElementById("cppButton");
  let javaButton = document.getElementById("javaButton");
  let haskellButton = document.getElementById("haskellButton");
  let rustEditor = document.getElementById("rustEditor");
  let cppEditor = document.getElementById("cppEditor");
  let javaEditor = document.getElementById("javaEditor");
  let haskellEditor = document.getElementById("haskellEditor");

  // Update displayed editor
  if (x === 0) {
    cppButton.className = "btn btn-primary";
    cppEditor.style.display = "";
    submitButton.onclick = function() {
      submit(0);
    };
  } else {
    cppButton.className = "btn btn-secondary";
    cppEditor.style.display = "none";
  }
  if (x === 1) {
    rustButton.className = "btn btn-primary";
    rustEditor.style.display = "";
    submitButton.onclick = function() {
      submit(1);
    };
  } else {
    rustButton.className = "btn btn-secondary";
    rustEditor.style.display = "none";
  }
  if (x === 2) {
    javaButton.className = "btn btn-primary";
    javaEditor.style.display = "";
    submitButton.onclick = function() {
      submit(2);
    };
  } else {
    javaButton.className = "btn btn-secondary";
    javaEditor.style.display = "none";
  }
  if (x === 3) {
    haskellButton.className = "btn btn-primary";
    haskellEditor.style.display = "";
    submitButton.onclick = function() {
      submit(3);
    };
  } else {
    haskellButton.className = "btn btn-secondary";
    haskellEditor.style.display = "none";
  }
}
function submit(x) {
  console.log(x);
  var editorName = "";
  if (x === 0) editorName = "cppEditor";
  else if (x === 1) editorName = "rustEditor";
  else if (x === 2) editorName = "javaEditor";
  else editorName = "haskellEditor";
  var editor = ace.edit(editorName);
  var code = editor.getValue();
  console.log(code);
  document.getElementById("spinner").style.visibility = "visible";
  let postdata = JSON.stringify({
    code: code
  });
  console.log(postdata);
  fetch("/" + editorName, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },
    body: postdata
  })
    .then(response => {
      console.log(response);
      return response.json();
    })
    .then(data => {
      document.getElementById("spinner").style.visibility = "hidden";
      document.getElementById("output").innerHTML = data.output;
      console.log(data.output);
    });
}

function App() {
  const handleClick = () => {
    console.log("Kompilerer");
  };
  return (
    <div className="App">
      <Typography variant="h1">Øving 3 - online kompilator</Typography>
      <Grid container direction="row" justify="center">
        <TextField
          id="input"
          label="Kode"
          multiline
          rows="4"
          defaultValue="Default Value"
          variant="outlined"
          fullWidth
        />
      </Grid>
      <Grid container direction="row" justify="center">
        <Button
          variant="contained"
          onClick={handleClick}
          style={{ margin: "20px" }}
        >
          Kompiler
        </Button>
      </Grid>
      <Grid container direction="row" justify="center">
        <TextField
          id="output"
          label="Output"
          multiline
          rows="4"
          defaultValue="Default Value"
          variant="outlined"
          fullWidth
        />
      </Grid>
    </div>
  );
}

export default App;
